mapper.select:
SELECT CASE WHEN A.CHILD_NAME != '' THEN A.CHILD_NAME ELSE (SELECT NAME FROM MEMBER_PARENT WHERE CELL_PHONE = A.CELL_PHONE) END AS RESERVATION_NAME, CASE WHEN LENGTH(A.CELL_PHONE) = 11 THEN CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,4),'-',SUBSTRING(A.CELL_PHONE,8,4)) ELSE CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,3),'-',SUBSTRING(A.CELL_PHONE,7,4)) END AS CELL_PHONE, DATE_FORMAT(B.SCHEDULE_START, '%Y/%m/%d') RESERVATION_DATE, DATE_FORMAT(B.SCHEDULE_START, '%H:%i') RESERVATION_TIME, (SELECT CODE_VALUE FROM COMMON_CODE WHERE CODE_TYPE = 'RESERVATION_STATUS' AND CODE_KEY = A.STATUS) RESERVATION_STATUS, A.RESERVATION_ID, (SELECT P.NAME FROM PLAY P WHERE P.PLAY_ID = B.PLAY_ID) PLAY_NAME, (SELECT SUBSTRING_INDEX(S.STORE_NAME, ' ', - 1) FROM STORE S WHERE S.STORE_ID = B.STORE_ID) STORE_NAME_SHORT FROM RESERVATION A, SCHEDULE B WHERE A.SCHEDULE_ID = B.SCHEDULE_ID AND A.CELL_PHONE = #{cellPhone}
AND B.STORE_ID = #{storeId}
AND B.SCHEDULE_START BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(DATE_FORMAT(NOW(),'%Y%m%d'), '000000'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') AND DATE_FORMAT(STR_TO_DATE(CONCAT(DATE_FORMAT(NOW(),'%Y%m%d'), '235959'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S')
AND DATE_FORMAT(B.SCHEDULE_START, '%H') = #{reservationTime}
ORDER BY STORE_NAME_SHORT, B.SCHEDULE_START
