mapper.select:
SELECT COUNT(*)
  FROM MEMBER_PARENT
 WHERE CELL_PHONE = #{parentCellPhone}
 
   AND CELL_PHONE != #{cellPhone}

mapper.update:
UPDATE MEMBER_PARENT
NAME = #{parentName}, CELL_PHONE = #{cellPhone}, EMAIL = #{email}, ADDRESS = #{address}, PASSWORD = #{password}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE CELL_PHONE = #{cellPhone}
