mapper.select:
SELECT SUB.SCHEDULE_ID, SUB.OPERATION_TYPE, SUB.NUMBER_OF_PEOPLE, SUB.TUTORING_NUMBER, SUB.SCHEDULE_START, SUB.SCHEDULE_END, SUB.SCHEDULE_START_TIME, SUB.SCHEDULE_TIME, SUB.SCHEDULE_DATE, SUB.PLAY_NAME, SUB.REG_ID, SUB.REG_DATE, SUB.UPD_ID, SUB.UPD_DATE, SUB.SUB_TOPIC, SUB.RESERVATION_CNT, CASE WHEN SUB.TUTORING_YN = 'Y' THEN (SUB.TUTORING_NUMBER - SUB.RESERVATION_CNT) ELSE (SUB.NUMBER_OF_PEOPLE - SUB.RESERVATION_CNT) END AS RESERVATION_AVA_COUNT FROM ( SELECT A.SCHEDULE_ID, A.OPERATION_TYPE, A.NUMBER_OF_PEOPLE, A.TUTORING_NUMBER, A.SCHEDULE_START, A.SCHEDULE_END, DATE_FORMAT(A.SCHEDULE_START,'%H:%i') SCHEDULE_START_TIME, CONCAT(DATE_FORMAT(A.SCHEDULE_START,'%H:%i'),'~',DATE_FORMAT(A.SCHEDULE_END,'%H:%i')) SCHEDULE_TIME, CONCAT(DATE_FORMAT(A.SCHEDULE_START,'%Y년 %m월 %d일'), ' 일정') SCHEDULE_DATE, (SELECT NAME FROM PLAY WHERE PLAY.PLAY_ID = A.PLAY_ID) PLAY_NAME, A.REG_ID, DATE_FORMAT(A.REG_DATE, '%Y/%m/%d') REG_DATE, A.UPD_ID, DATE_FORMAT(A.UPD_DATE, '%Y/%m/%d') UPD_DATE, A.SUB_TOPIC, CASE WHEN B.CLASS_TYPE = 'CLASS_D' THEN (SELECT COUNT(*) FROM RESERVATION WHERE SCHEDULE_ID = A.SCHEDULE_ID AND TUTORING_YN = 'Y' AND STATUS = '02') ELSE (SELECT COUNT(*) FROM RESERVATION WHERE SCHEDULE_ID = A.SCHEDULE_ID AND TUTORING_YN = 'N' AND STATUS = '02') END AS RESERVATION_CNT, CASE WHEN B.CLASS_TYPE = 'CLASS_D' THEN 'Y' ELSE 'N' END AS TUTORING_YN FROM SCHEDULE A, STORE_PLAY B
AND A.STORE_ID = B.STORE_ID
AND A.STORE_ID = #{storeId}
AND B.CLASS_TYPE = #{classType}
AND A.SCHEDULE_START BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '000000'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') AND DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '235959'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S')
AND A.SCHEDULE_START > NOW() AND A.SCHEDULE_START BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '000000'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') AND DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '235959'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S')
AND A.SCHEDULE_END BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '000000'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') AND NOW()
AND DATE_FORMAT(A.SCHEDULE_START, '%H') % 2 = 0
) SUB
