mapper.select:
SELECT PROGRAM_ID, CHAIN_CLASS, (SELECT NAME FROM PLAY P WHERE P.PLAY_ID = CHAIN_CLASS) CHAIN_CLASS_NAME, PROGRAM_NAME, PROGRAM_CONTENTS, THUMBNAIL_ID, PICTURE_ID, TEACHING_PLAN_IMG_ID, STATUS, REG_ID, DATE_FORMAT(REG_DATE, '%Y-%m-%d') REG_DATE, UPD_ID, DATE_FORMAT(UPD_DATE, '%Y-%m-%d') UPD_DATE FROM STORE_PROGRAM
AND STATUS = #{status}
ORDER BY REG_DATE DESC
LIMIT #{limit}

mapper.update:
UPDATE STORE_PROGRAM
CHAIN_CLASS = #{chainClass}, PROGRAM_NAME = #{programName}, PROGRAM_CONTENTS = #{programContents}, THUMBNAIL_ID = #{thumbnailId}, PICTURE_ID = #{pictureId}, TEACHING_PLAN_IMG_ID = #{teachingPlanImgId}, STATUS = #{status}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE PROGRAM_ID = #{programId}
