mapper.update:
UPDATE VOUCHER
GRADE = #{grade}, TICKET_NAME = #{ticketName}, PRICE = #{price}, SALE_PRICE = #{salePrice}, USE_TIME = #{useTime}, END_DATE = DATE_FORMAT(DATE_ADD(NOW(), INTERVAL #{endDate} MONTH), '%Y%m%d'), IMAGE_IDX = #{imageIdx}, STATUS = #{status}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_NO = #{voucherNo}

UPDATE VOUCHER_SALE
BUY_CELL_PHONE = #{buyCellPhone}, USED_CHILDREN_NAME = #{usedChildrenName}, PG_TYPE = #{pgType}, APPROVAL_NO = #{approvalNo}, STORE_ID = #{storeId}, BUY_CELL_PHONE = #{buyCellPhone}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_NO = #{voucherNo}

UPDATE VOUCHER_USE
VOUCHER_NO = #{voucherNo}, USE_TIME = #{useTime}, STATUS = #{status}, SCHEDULE_ID = #{scheduleId}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_USE_ID = #{voucherUseId}
